template <class T>
class string_reference : range_fns {
  using string_view_t = string_reference<add_const<T>>;

  T *p = nullptr;
  size_t n = 0u;

  using range_fns::begin;
  using range_fns::end;
  using range_fns::rbegin;
  using range_fns::rend;
  using range_fns::empty;
  using range_fns::size;

public:
  using pointer = T *;
  using const_pointer = const T *;
  using value_type = remove_cv<T>;
  using reference = T &;
  using const_reference = const T &;
  using iterator = T *;
  using const_iterator = const T *;
  using difference_type = ptrdiff_t;
  using size_type = size_t;

  string_reference() = default;
  ~string_reference() = default;
  string_reference(const string_reference &) = default;
  string_reference &operator =(const string_reference &) = default;
  string_reference(string_reference &&) = default;
  string_reference &operator =(string_reference &&) = default;

  constexpr string_reference(nullptr_t) : p(nullptr), n(0) {}
  constexpr string_reference &operator =(nullptr_t) {
    p = nullptr;
    n = 0;
    return *this;
  }
  constexpr void clear() {
    *this = nullptr;
  }

  template <class TT,
            class = enable_if
            <(is_same<TT, T>
              || (is_const<T> && is_same<TT, remove_const<T>>))>>
  constexpr string_reference(TT &x) : p(addressof(x)), n(1) {}
  template <class RANGE,
            class = enable_if
            <(!is_same<decay<RANGE>, string_reference>
              && is_rng<RANGE &&> && !is_array<remove_reference<RANGE>>
              && is_same<rng_vt<RANGE>, value_type>
              && is_citr<rng_itr<RANGE>>
              && is_convertible<rng_ref<RANGE>, T>
              && is_convertible<rng_ptr<RANGE>, T *>)>>
  constexpr string_reference(RANGE &&r)
    : string_reference(begin(r), end(r)) {}
  template <class RANGE,
            class = enable_if
            <(!is_same<decay<RANGE>, string_reference>
              && is_rng<RANGE &&> && is_array<remove_reference<RANGE>>
              && extent<remove_reference<RANGE>> != 0u
              && is_convertible<rng_ptr<RANGE>, T *>)>,
            class = void>
  constexpr string_reference(RANGE &&r) noexcept
    : string_reference(begin(r), *(end(r) - 1) != 0 ? end(r) : end(r) - 1) {}

  template <class X,
            class = enable_if
            <(is_same<itr_vt<X>, value_type> && is_citr<X>
              && is_convertible<itr_ref<X>, T>
              && is_convertible<itr_ptr<X>, T *>)>>
  constexpr string_reference(X x, X y) : p(to_address(x)), n(y - x) {}
  template <class X, class Y,
            class = enable_if
            <(is_same<itr_vt<X>, value_type> && is_citr<X>
              && is_convertible<itr_ref<X>, T>
              && is_convertible<itr_ptr<X>, T *>
              && is_integral<Y>)>,
            class = void>
  constexpr string_reference(X x, Y y) : p(to_address(x)), n(y) {}
  template <class X, class Y, class Z,
            class = enable_if
            <(is_rng<X> && is_same<rng_vt<X>, value_type>
              && is_convertible<rng_ref<X>, T>
              && is_convertible<rng_ptr<X>, T *>
              && is_integral<Y> && is_integral<Z>)>>
  constexpr string_reference(X &&x, Y y, Z z)
    : p(to_address(begin(x) + y)), n(z) {}

  template <class TT = T, class = enable_if<is_const<TT>>>
  constexpr
  string_reference(const string_reference<remove_const<T>> &x) noexcept {
    p = x.p;
    n = x.n;
  }

  template <class X, class TT = T,
            class = enable_if
            <(is_same<remove_cvref<X>, TT *>
              || (is_const<TT>
                  && is_same<remove_reference<X>, remove_const<TT> *>))>,
            bool = 0>
  constexpr string_reference(X &&s) noexcept {
    const remove_const<T> zero_c = 0;
    p = s;
    auto p2 = s;
    for (; *p2 != zero_c; ++p2)
      ;
    n = p2 - p;
  }

  constexpr T *begin() const noexcept {
    return p;
  }
  constexpr T *end() const noexcept {
    return p + n;
  }
  constexpr size_t size() const noexcept {
    return n;
  }
  constexpr bool empty() const noexcept {
    return n == 0;
  }

  constexpr T *data() const noexcept {
    return p;
  }

  constexpr T *cbegin() const noexcept {
    return begin();
  }
  constexpr T *cend() const noexcept {
    return end();
  }

  constexpr reference operator [](size_type nn) noexcept {
    return p[nn];
  }
  constexpr const_reference operator [](size_type nn) const noexcept {
    return p[nn];
  }
  constexpr reference at(size_type nn) {
    if (nn >= size())
      throw_or_terminate<out_of_range>("re::string_reference: at(n) failed\n");
    return p[nn];
  }
  constexpr const_reference at(size_type nn) const {
    if (nn >= size())
      throw_or_terminate<out_of_range>("re::string_reference: at(n) failed\n");
    return p[nn];
  }
  constexpr reference front() {
    return *p;
  }
  constexpr reference back() {
    return *(p + n - 1);
  }
  constexpr const_reference front() const {
    return *p;
  }
  constexpr const_reference back() const {
    return *(p + n - 1);
  }

  using reverse_iterator = re::reverse_iterator<iterator>;
  using const_reverse_iterator = re::reverse_iterator<const_iterator>;

  constexpr reverse_iterator rbegin() noexcept {
    return reverse_iterator(end());
  }
  constexpr reverse_iterator rend() noexcept {
    return reverse_iterator(begin());
  }
  constexpr const_reverse_iterator rbegin() const noexcept {
    return const_reverse_iterator(end());
  }
  constexpr const_reverse_iterator rend() const noexcept {
    return const_reverse_iterator(begin());
  }
  constexpr const_reverse_iterator crbegin() const noexcept {
    return rbegin();
  }
  constexpr const_reverse_iterator crend() const noexcept {
    return rend();
  }
};
template <class T1, class T2>
enable_if<is_same<remove_const<T1>, remove_const<T2>>, bool>
operator ==(const string_reference<T1> &x,
            const string_reference<T2> &y) noexcept {
  if constexpr (!is_same<remove_const<T1>, char>) {
    return equal(x, y);
  }
  else {
    return x.size() == y.size() && memcmp(x.begin(), y.begin(), x.size()) == 0;
  }
}
template <class T1, class T2>
enable_if<is_same<remove_const<T1>, remove_const<T2>>,
          synth_3way_result<T1, T2>>
operator <=>(const string_reference<T1> &x,
             const string_reference<T2> &y) noexcept {
  if constexpr (!is_same<remove_const<T1>, char>) {
    return lexicographical_synth_3way(x, y);
  }
  else {
    const auto x_sz = x.size();
    const auto y_sz = y.size();
    if (x_sz == y_sz) {
      const auto c = memcmp(x.begin(), y.begin(), x.size());
      if (c < 0)
        return strong_lt;
      else if (c > 0)
        return strong_gt;
      else
        return strong_eq;
    }
    else if (x_sz < y_sz) {
      const auto c = memcmp(x.begin(), y.begin(), x.size());
      return (c <= 0) ? strong_lt : strong_gt;
    }
    else {
      const auto c = memcmp(y.begin(), x.begin(), y.size());
      return (c <= 0) ? strong_gt : strong_lt;
    }
  }
}

