    auto it = x.v.begin();
    ret.v = vv.exchange(i1.base(), i2.base(), move(x.v));
    for (; it != i2.base(); ++it)
      iterator(it).parent(p);
    for (auto &it2 : iters(ret))
      it2.parent(iterator{});
    return ret;
