  friend struct inner::fo_good;
  static bool good(const vec_t &) {
    return true;
  }
  static bool good(const vec_t &v)
    requires requires (const vec_t &vv) {inner::good(vv);} {
    return inner::good(v);
  }
  bool good() const {
    if (!good(v))
      return false;
    if (const_iterator(v.end()).parent() != iterator{})
      return false;
    for (auto &i : iters(v)) {
      const iterator it(i.to_mutable());
      if (it.parent() != iterator{})
        return false;
      for (auto &it2 : iters(it.pre_order())) {
        auto &x = it2.base().tree_node();
        if (!good(x.v))
          return false;
        if (x.v.get_allocator() != get_allocator())
          return false;
        if (const_iterator(x.v.end()).parent() != x.iter())
          return false;
        if (any_of(x.v, [&x](auto &y) {return y.parent() != x.iter();}))
          return false;
      }
    }
    return true;
  }
